import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Dashboard() {
  const [orders, setOrders] = useState([]);
  const [totalCost, setTotalCost] = useState(0);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/getAllOrders')
      .then(response => response.json())
      .then(data => {
        setOrders(data);
        const total = data.reduce((sum, order) => sum + parseFloat(order.total), 0);
        setTotalCost(total);
      })
      .catch(error => console.error('Error fetching orders:', error));
  }, []);

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setShowDetailsModal(true);
  };

  const handleCloseModal = () => {
    setShowDetailsModal(false);
    setSelectedOrder(null);
  };

  return (
    <div className="right content-page">
      <div className="body content rows scroll-y">
        <form className="form-horizontal">
          <div className="box-info full" id="taskFormContainer">
            <h2>Grocery Store Management System</h2>
            <div className="panel-body pt-0">
              <div className="row mb-4">
                <div className="col-sm-12">
                  <Link to="/order" className="btn btn-primary pull-right ml-3" style={{ marginRight: '25px', textDecoration: 'none' }}>
                    New Order
                  </Link>
                  <Link to="/manage-product" className="btn btn-primary pull-right" style={{ textDecoration: 'none' }}>
                    Manage Products
                  </Link>
                </div>
              </div>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Order Number</th>
                    <th>Customer Name</th>
                    <th>Total Cost</th>
                    <th style={{ width: '120px' }}>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.map(order => (
                    <tr key={order.order_id}>
                      <td>{new Date(order.datetime).toUTCString()}</td>
                      <td>{order.order_id}</td>
                      <td>{order.customer_name}</td>
                      <td>{parseFloat(order.total).toFixed(2)} Rs</td>
                      <td>
                        <button
                          type="button"
                          className="btn btn-xs btn-primary"
                          onClick={() => handleViewDetails(order)}
                        >
                          View Details
                        </button>
                      </td>
                    </tr>
                  ))}
                  <tr>
                    <td colSpan="4" style={{ textAlign: 'end' }}><b>Total</b></td>
                    <td><b>₹ {totalCost.toFixed(2)}</b></td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </form>
      </div>

      {showDetailsModal && selectedOrder && (
        <div className="modal fade-scale show" role="dialog" data-backdrop="static">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h4 className="modal-title">Order Details - Order #{selectedOrder.order_id}</h4>
              </div>
              <div className="modal-body">
                <table className="table table-bordered">
                  <thead>
                    <tr>
                      <th>Product Name</th>
                      <th>Quantity</th>
                      <th>Price Per Unit (Rs)</th>
                      <th>Total Price (Rs)</th>
                    </tr>
                  </thead>
                  <tbody>
                    {selectedOrder.order_details.map((detail, index) => (
                      <tr key={index}>
                        <td>{detail.product_name}</td>
                        <td>{detail.quantity}</td>
                        <td>{parseFloat(detail.price_per_unit).toFixed(2)}</td>
                        <td>{parseFloat(detail.total_price).toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Dashboard;
