import React, { useState, useEffect } from 'react';

const Order = () => {
  // Replace with your original state variables
  const [products, setProducts] = useState([]); // Initialize as empty array
  const [uoms, setUoms] = useState([]); // Initialize as empty array
  const [customerName, setCustomerName] = useState('');
  const [orderItems, setOrderItems] = useState([]); // Adjust based on your original state

  // Fetch products
  useEffect(() => {
    fetch('/getProducts')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched products:', data); // Debug
        if (Array.isArray(data)) {
          setProducts(data);
        } else {
          console.error('Products data is not an array:', data);
          setProducts([]);
        }
      })
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  // Fetch UOMs
  useEffect(() => {
    fetch('/getUOM')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched UOMs:', data); // Debug
        if (Array.isArray(data)) {
          setUoms(data);
        } else {
          console.error('UOMs data is not an array:', data);
          setUoms([]);
        }
      })
      .catch(error => console.error('Error fetching UOMs:', error));
  }, []);

  // Replace with your original addItem and saveOrder logic
  const addItem = () => {
    // Your original addItem logic here
    console.log('Add item triggered');
  };

  const saveOrder = () => {
    // Your original saveOrder logic here
    console.log('Save order triggered');
  };

  // Replace with your original return JSX
  return (
    <div>
      <h2>New Order</h2>
      {/* Restore your original UI here, e.g., */}
      <div>
        <label>Customer Name:</label>
        <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
      </div>
      {/* Example: Add product selection with conditional rendering */}
      <div>
        <label>Product:</label>
        <select>
          {Array.isArray(products) && products.length > 0 ? (
            products.map(product => (
              <option key={product.product_id} value={product.product_id}>
                {product.name}
              </option>
            ))
          ) : (
            <option disabled>No products available</option>
          )}
        </select>
      </div>
      {/* Add other original fields (quantity, UOM, etc.) with similar conditional rendering */}
      <button onClick={addItem}>Add Item</button>
      <button onClick={saveOrder}>Save Order</button>
      {/* Restore your order items display */}
    </div>
  );
};

export default Order;
