import React, { useState, useEffect } from 'react';
import ProductModal from './ProductModal';

function ManageProduct() {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/getProducts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const handleDelete = (productId, productName) => {
    if (window.confirm(`Are you sure to delete ${productName} item?`)) {
      fetch('http://127.0.0.1:5000/deleteProduct', {
        method: 'POST',
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        body: `product_id=${productId}`
      })
        .then(() => {
          setProducts(products.filter(product => product.product_id !== productId));
        })
        .catch(error => console.error('Error deleting product:', error));
    }
  };

  const handleUpdate = (productId, productName) => {
    // Logic for updating the product can be implemented here
    console.log(`Update product with ID: ${productId}, Name: ${productName}`);
    // You can open a modal or redirect to another page for updating the product
    // For now, just logging the product ID and name
    // You can implement the update logic here
    // For example, you can open a modal with a form to update the product details
    // Or redirect to another page with the product ID in the URL
    // For now, just logging the product ID and name
    console.log(`Update product with ID: ${productId}, Name: ${productName}`);
  };

  return (
    <div className="right content-page">
      <div className="body content rows scroll-y">
        <div className="box-info full" id="taskFormContainer">
          <h2>Manage Products</h2>
          <div className="panel-body pt-0">
            <div className="row mb-4">
              <div className="col-sm-12">
                <button
                  type="button"
                  className="btn btn-sm btn-primary pull-right"
                    style={{ marginRight: '25px' }}
                  onClick={() => setShowModal(true)}
                >
                  Add New Product
                </button>
              </div>
            </div>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Unit</th>
                  <th>Price Per Unit</th>
                  <th style={{ width: '150px' }}>Action</th>
                </tr>
              </thead>
              <tbody>
                {products.map(product => (
                  <tr key={product.product_id}>
                    <td>{product.name}</td>
                    <td>{product.uom_name}</td>
                    <td>{product.price_per_unit}</td>
                    <td>
                      <button
                        type="button" class="btn btn-danger"
                        onClick={() => handleDelete(product.product_id, product.name)}
                      >
                        Delete
                      </button>
                      &nbsp;
                      &nbsp;
                      <button 
                        type="button" class="btn btn-warning">
                        Update
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      {showModal && (
        <ProductModal
          onClose={() => setShowModal(false)}
          onSave={() => {
            setShowModal(false);
            fetch('http://127.0.0.1:5000/getProducts')
              .then(response => response.json())
              .then(data => setProducts(data))
              .catch(error => console.error('Error fetching products:', error));
          }}
        />
      )}
    </div>
  );
}

export default ManageProduct;
