import React, { useEffect, useState } from 'react';
import '../styles/manage-products.css';
import { getProducts, insertProduct, deleteProduct } from '../services/api';

const ManageProducts = () => {
  const [products, setProducts] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    uom_id: '',
    price_per_unit: ''
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    const response = await getProducts();
    setProducts(response.data);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await insertProduct(formData);
    fetchProducts();
  };

  const handleDelete = async (id) => {
    await deleteProduct(id);
    fetchProducts();
  };

  return (
    <div className="manage-products">
      <h2>Manage Products</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Product Name" onChange={handleChange} required />
        <input name="uom_id" placeholder="UOM ID" onChange={handleChange} required />
        <input name="price_per_unit" placeholder="Price per Unit" onChange={handleChange} required />
        <button type="submit">Add Product</button>
      </form>
      <ul>
        {products.map((product) => (
          <li key={product.product_id}>
            {product.name} - {product.price_per_unit}
            <button onClick={() => handleDelete(product.product_id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageProducts;
