import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Dashboard() {
  const [orders, setOrders] = useState([]);
  const [totalCost, setTotalCost] = useState(0);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/getAllOrders')
      .then(response => response.json())
      .then(data => {
        setOrders(data);
        const total = data.reduce((sum, order) => sum + parseFloat(order.total), 0);
        setTotalCost(total);
      })
      .catch(error => console.error('Error fetching orders:', error));
  }, []);

  return (
    <div className="right content-page">
      <div className="body content rows scroll-y">
        <form className="form-horizontal">
          <div className="box-info full" id="taskFormContainer">
            <h2>Grocery Store Management System</h2>
            <div className="panel-body pt-0">
              <div className="row mb-4">
                <div className="col-sm-12">
                  <Link to="/order" className="btn btn-primary pull-right ml-3" style={{ marginRight: '25px'}}>
                    New Order
                  </Link>
                  <Link to="/manage-product" className="btn btn-primary pull-right">
                    Manage Products
                  </Link>
                </div>
              </div>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Order Number</th>
                    <th>Customer Name</th>
                    <th>Total Cost</th>
                  </tr>
                </thead>
                <tbody>
                  {orders.map(order => (
                    <tr key={order.order_id}>
                      <td>{new Date(order.datetime).toLocaleString()}</td>
                      <td>{order.order_id}</td>
                      <td>{order.customer_name}</td>
                      <td>{parseFloat(order.total).toFixed(2)} Rs</td>
                    </tr>
                  ))}
                  <tr>
                    <td colSpan="3" style={{ textAlign: 'end' }}><b>Total</b></td>
                    <td><b>{totalCost.toFixed(2)} Rs</b></td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Dashboard;
