import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';
import Dashboard from './Dashboard';
import ManageProduct from './ManageProduct';
import Order from './Order';

const AdminPage = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const adminCredentials = {
        username: "Rohith", // Replace with your desired admin username
        password: "Ro2001#", // Replace with your desired admin password
    };

    const handleLogin = (e) => {
        e.preventDefault();
        if (username === adminCredentials.username && password === adminCredentials.password) {
            setIsAuthenticated(true);
        } else {
            alert("Invalid username or password");
            window.location.reload(); // Reload the page on failed login
        }
    };

    if (isAuthenticated) {
        return (
            <div>
                <div style={{ 
                    display: 'flex', 
                    flexDirection: 'row', 
                    alignItems: 'center', 
                    justifyContent: 'center', 
                    justifyItems: 'center',
                    paddingl: '10px', 
                    margin: 0,
                    marginLeft: '20px',
                    marginRight: '20px',
                    borderRadius: '10px',
                    backgroundColor: 'white',
                    width: 'auto',
                    opacity: 0.8
                    }}>
                    <h1 style={{color: 'black', fontSize: '18px'}}>Welcome, {username} ! logged in as an Admin.</h1>
                </div>
                <BrowserRouter>
                <div className="container">
                    <Navbar />
                    <Routes>
                    <Route path="/" element={<Dashboard />} />
                    <Route path="/manage-product" element={<ManageProduct />} />
                    <Route path="/order" element={<Order />} />
                    </Routes>
                </div>
                </BrowserRouter>
            </div>
        );
    }

    return (
        <div>
            <h1>Admin Login</h1>
            <form onSubmit={handleLogin}>
                <div>
                    <label>Username:</label>
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default AdminPage;
