import React, { useState, useEffect } from 'react';
import ProductModal from './ProductModal';

function ManageProduct() {
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetch('http://127.0.0.1:5000/getProducts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const handleDelete = async (productId, productName) => {
    if (window.confirm(`Are you sure to delete ${productName} item?`)) {
      try {
        const response = await fetch('http://127.0.0.1:5000/deleteProduct', {
          method: 'POST',
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          body: `product_id=${productId}`
        });
        const result = await response.json();
        if (result.error) {
          alert(result.error);
        } else {
          setProducts(products.filter(product => product.product_id !== productId));
        }
      } catch (error) {
        console.error('Error deleting product:', error);
        alert('An error occurred while deleting the product.');
      }
    }
  };

  const handleUpdate = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleModalClose = () => {
    setShowModal(false);
    setSelectedProduct(null);
  };

  const handleModalSave = () => {
    setShowModal(false);
    setSelectedProduct(null);
    fetch('http://127.0.0.1:5000/getProducts')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  };

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);

  };

  

  return (
    <div className="right content-page">
      <div className="body content rows scroll-y">
        <div className="box-info full" id="taskFormContainer">
          <h2>Manage Products</h2>
          <div className="panel-body pt-0">
            <div className="row mb-4">
              <div className="col-sm-12">
                <button
                  type="button"
                  className="btn btn-sm btn-success pull-right"
                  style={{ marginRight: '28px', fontSize: '14px', fontWeight: '500' }}
                  onClick={() => setShowModal(true)}
                >
                  Add New Product
                </button>
              </div>
            </div>
            <div className="search-bar">
              <input
                type="text"
                placeholder="Search by name or email..."
                value={searchQuery}
                onChange={handleSearchChange}
              />
            </div>
            <table className="table table-bordered">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Unit</th>
                  <th>Price Per Unit</th>
                  <th style={{ width: '150px' }}>Action</th>
                </tr>
              </thead>
              <tbody>
                {products.map(product => (
                  <tr key={product.product_id}>
                    <td>{product.name}</td>
                    <td>{product.uom_name}</td>
                    <td>{product.price_per_unit}</td>
                    <td>
                    <button
                        type="button"
                        className="btn btn-xs btn-warning"
                        style={{ marginLeft: '0px', fontSize: '14px', fontWeight: '500' }}
                        onClick={() => handleUpdate(product)}
                      >
                        Update
                      </button>
                      &nbsp;
                      &nbsp;
                      <button
                        type="button"
                        className="btn btn-xs btn-danger"
                        style={{ marginLeft: '0px', fontSize: '14px', fontWeight: '500' }}
                        onClick={() => handleDelete(product.product_id, product.name)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      {showModal && (
        <ProductModal
          onClose={handleModalClose}
          onSave={handleModalSave}
          product={selectedProduct}
        />
      )}
    </div>
  );
}

export default ManageProduct;
