const { useState, useEffect } = React;

function ProductModal({ onClose, onSave }) {
  const [formData, setFormData] = useState({
    name: '',
    uom_id: '',
    price_per_unit: ''
  });
  const [uoms, setUoms] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:5000/getUOM')
      .then(response => response.json())
      .then(data => setUoms(data))
      .catch(error => console.error('Error fetching UOMs:', error));
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = () => {
    const requestPayload = {
      product_name: formData.name,
      uom_id: formData.uom_id,
      price_per_unit: formData.price_per_unit
    };
    fetch('http://127.0.0.1:5000/insertProduct', {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: `data=${JSON.stringify(requestPayload)}`
    })
      .then(() => {
        onSave();
      })
      .catch(error => console.error('Error saving product:', error));
  };

  return (
    <div className="modal fade-scale show" role="dialog" data-backdrop="static">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h4 className="modal-title">Add New Product</h4>
          </div>
          <div className="modal-body">
            <form id="productForm">
              <input type="hidden" name="id" value="0" />
              <div className="row">
                <div className="col-sm-12">
                  <div className="form-group">
                    <label>Name</label>
                    <input
                      className="form-control"
                      placeholder="Name"
                      name="name"
                      type="text"
                      value={formData.name}
                      onChange={handleChange}
                    />
                  </div>
                </div>
                <div className="col-sm-12">
                  <div className="form-group">
                    <label>Unit</label>
                    <select
                      name="uom_id"
                      className="form-control"
                      value={formData.uom_id}
                      onChange={handleChange}
                    >
                      <option value="">--Select--</option>
                      {uoms.map(uom => (
                        <option key={uom.uom_id} value={uom.uom_id}>
                          {uom.uom_name}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                <div className="col-sm-12">
                  <div className="form-group">
                    <label>Price Per Unit</label>
                    <input
                      className="form-control"
                      placeholder="Price Per Unit"
                      name="price_per_unit"
                      type="text"
                      value={formData.price_per_unit}
                      onChange={handleChange}
                    />
                  </div>
                </div>
              </div>
            </form>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" onClick={onClose}>
              Close
            </button>
            <button type="button" className="btn btn-primary" onClick={handleSave}>
              Save
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
