import React, { useState, useEffect } from 'react';

const Order = () => {
  const [products, setProducts] = useState([]); // Initialize as empty array
  const [uoms, setUoms] = useState([]); // Initialize as empty array
  const [selectedProduct, setSelectedProduct] = useState('');
  const [quantity, setQuantity] = useState('');
  const [selectedUom, setSelectedUom] = useState('');
  const [orderItems, setOrderItems] = useState([]);
  const [customerName, setCustomerName] = useState('');

  // Fetch products
  useEffect(() => {
    fetch('/getProducts')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched products:', data); // Debug
        if (Array.isArray(data)) {
          setProducts(data); // Only set if it's an array
        } else {
          console.error('Products data is not an array:', data);
          setProducts([]); // Default to empty array if invalid
        }
      })
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  // Fetch UOMs
  useEffect(() => {
    fetch('/getUOM')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Fetched UOMs:', data); // Debug
        if (Array.isArray(data)) {
          setUoms(data); // Only set if it's an array
        } else {
          console.error('UOMs data is not an array:', data);
          setUoms([]); // Default to empty array if invalid
        }
      })
      .catch(error => console.error('Error fetching UOMs:', error));
  }, []);

  const addItem = () => {
    if (selectedProduct && quantity && selectedUom) {
      const product = products.find(p => p.product_id === parseInt(selectedProduct));
      const totalPrice = product ? product.price_per_unit * parseFloat(quantity) : 0;
      setOrderItems([...orderItems, { product_id: parseInt(selectedProduct), quantity: parseFloat(quantity), uom_id: parseInt(selectedUom), total_price: totalPrice }]);
      setSelectedProduct('');
      setQuantity('');
      setSelectedUom('');
    }
  };

  const saveOrder = () => {
    const orderData = {
      customer_name: customerName,
      items: orderItems,
    };
    fetch('/insertOrder', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `data=${JSON.stringify(orderData)}`,
    })
      .then(response => response.json())
      .then(data => console.log('Order saved:', data))
      .catch(error => console.error('Error saving order:', error));
  };

  return (
    <div>
      <h2>New Order</h2>
      <div>
        <label>Customer Name:</label>
        <input type="text" value={customerName} onChange={(e) => setCustomerName(e.target.value)} />
      </div>
      <div>
        <label>Product:</label>
        <select value={selectedProduct} onChange={(e) => setSelectedProduct(e.target.value)}>
          <option value="">Select a product</option>
          {products.length > 0 ? ( // Conditional rendering
            products.map(product => (
              <option key={product.product_id} value={product.product_id}>
                {product.name} (${product.price_per_unit})
              </option>
            ))
          ) : (
            <option disabled>No products available</option>
          )}
        </select>
      </div>
      <div>
        <label>Quantity:</label>
        <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
      </div>
      <div>
        <label>UOM:</label>
        <select value={selectedUom} onChange={(e) => setSelectedUom(e.target.value)}>
          <option value="">Select a UOM</option>
          {uoms.length > 0 ? ( // Conditional rendering
            uoms.map(uom => (
              <option key={uom.uom_id} value={uom.uom_id}>
                {uom.uom_name}
              </option>
            ))
          ) : (
            <option disabled>No UOMs available</option>
          )}
        </select>
      </div>
      <button onClick={addItem}>Add Item</button>
      <div>
        <h3>Order Items</h3>
        {orderItems.map((item, index) => (
          <div key={index}>
            {products.find(p => p.product_id === item.product_id)?.name} - Qty: {item.quantity} {uoms.find(u => u.uom_id === item.uom_id)?.uom_name} - Total: ${item.total_price}
          </div>
        ))}
      </div>
      <button onClick={saveOrder} disabled={orderItems.length === 0}>Save Order</button>
    </div>
  );
};

export default Order;
